mapper.update:

UPDATE unidad u
JOIN colaborador c ON c.noPersonal = #{noPersonal}
SET u.idColaborador = c.idColaborador
WHERE u.numeroInterno = #{numeroInterno} 
AND u.situacion = 'activo';


UPDATE unidad
SET idColaborador = NULL
WHERE idColaborador = #{idColaborador}

mapper.select:
SELECT numeroInterno 
FROM unidad 
WHERE idColaborador = #{idColaborador}
